<script lang="ts" setup>
import { ref, onMounted, watch } from 'vue';
import { useRouter } from 'vue-router';

const router = useRouter();

interface NavItem {
  name: string;
  label: string;
}

const activeIndex = ref('/');
const navItems: NavItem[] = [
  { name: '/', label: '首页' },
  { name: '/products', label: '产品' },
  { name: '/services', label: '服务' },
  { name: '/about', label: '关于我们' }
];

const Links = (key: string) => {
  const item = navItems.find((i) => i.name === key);
  if (item) {
    activeIndex.value = item.name;
    router.push(item.name);
  }
}

interface FooterItem {
  key: string;
  label: string;
}

const aboutItems: FooterItem[] = [
  { key: 'about', label: '团队介绍' },
  { key: '', label: '加入我们' },
  { key: 'sponsor-us', label: '赞助我们' }
];

const productItems: FooterItem[] = [
  { key: 'https://box3.codestarlabs.top/blockly3/', label: '新blockly3' },
  { key: 'https://box3.codestarlabs.top/blockly/', label: '旧blockly3' },
  { key: 'https://chatapi.codestarlabs.top/', label: 'ChatAPI' },
  { key: 'https://docker.codestarlabs.top/', label: 'Docker镜像' },
  { key: 'https://chat.codestarlabs.top/', label: 'ChatAPI实验室' },
];

const serviceItems: FooterItem[] = [
  { key: 'service1', label: '服务1' },
  { key: 'service2', label: '服务2' },
  { key: 'service3', label: '服务3' }
];

const scrollToSection = (sectionId: string) => {
  const element = document.getElementById(sectionId);
  if (element) {
    element.scrollIntoView({ behavior: 'smooth' });
  }
};

const scrollToSectiona = (key: string) => {
  if (key === 'about') {
    router.push('/about');
    scrollToSection('about');
    window.scrollTo({ top: 30, behavior: 'smooth' });
  }
}

const handleClick = (key: string) => {
  window.location.href = key;
};

const handleMenuClick = (key: string) => {
  Links(key);
};

const Login = () => {
  window.location.href = 'https://codestarlabs.top/login/';
}

const home = () => {
  activeIndex.value = '/';
  router.push('/');
};

onMounted(() => {
  setActiveIndex(router.currentRoute.value.path);
});

watch(() => router.currentRoute.value.path, (newPath) => {
  setActiveIndex(newPath);
});

const setActiveIndex = (path: string) => {
  const item = navItems.find((i) => i.name === path);
  if (item) {
    activeIndex.value = item.name;
  } else {
    activeIndex.value = '/';
  }
};
</script>

