<script lang="ts" setup>
import { onMounted } from 'vue';
import gsap from 'gsap';
import ScrollTrigger from 'gsap/ScrollTrigger';

interface TeamMember {
  name: string;
  avatar: string;
  roles: string[];
  GithubUrl: string;
}

const aboutText = `Code Star Labs（码星Labs或码星实验室）是一个高质量的青少年编程云团队，这里面聚集着众多编程大佬。我们在这里一起学习和相互交流，以提升自己的各项技能。同时，我们都以编程为爱好，共同研究和讨论各种编程技术问题。目前我们已有30+的成员，且在各成员以及与其他团队的合作下开发出了一系列优质的产品。Code Star Labs 非常欢迎你的加入！`;

const teamMembers: TeamMember[] = [
  { 
    name: 'CoderMeowHacker', 
    avatar: 'https://avatars.githubusercontent.com/u/158447241?v=4',
    roles: ['创始人'],
    GithubUrl: 'https://github.com/codemiao08',
  },
  {
    name: 'Soul',
    avatar: 'https://avatars.githubusercontent.com/u/155857563?v=4',
    roles: ['前端UI', '管理员'],
    GithubUrl: 'https://github.com/SoulCodingYanhun',
  },
];

const github = (url: string) => {
  location.href = url;
}

// Register ScrollTrigger plugin
gsap.registerPlugin(ScrollTrigger);

onMounted(() => {
  // Hero section animation
  gsap.from('.hero-title', { 
    duration: 1, 
    y: 50, 
    opacity: 0, 
    ease: 'power3.out' 
  });
  gsap.from('.hero-subtitle', { 
    duration: 1, 
    y: 30, 
    opacity: 0, 
    delay: 0.5, 
    ease: 'power3.out' 
  });
  // gsap.from('.cta-button', { 
  //   duration: 1, 
  //   y: 20, 
  //   opacity: 0, 
  //   delay: 1, 
  //   ease: 'power3.out' 
  // });

  // About section animation
  gsap.from('.about-card', {
    scrollTrigger: {
      trigger: '.about-section',
      start: 'top 80%',
    },
    duration: 1,
    y: 50,
    opacity: 0,
    ease: 'power3.out'
  });

  // Timeline animation
  gsap.from('.timeline-container', {
    scrollTrigger: {
      trigger: '.timeline-section',
      start: 'top 80%',
    },
    duration: 1,
    opacity: 0,
    ease: 'power3.out'
  });

  // Vision and Collaboration sections parallax effect
  gsap.to('.vision-section, .collaboration-section', {
    scrollTrigger: {
      trigger: '.vision-section',
      start: 'top bottom',
      end: 'bottom top',
      scrub: true,
    },
    backgroundPosition: '50% 100%',
    ease: 'none'
  });
});

</script>

